'use strict';

var checkAndEvaluateMath = require('../checkAndEvaluateMath.cjs');
var transformDimension = require('../transformDimension.cjs');
var transformHEXRGBa = require('./transformHEXRGBa.cjs');

/**
 * Helper: Transforms border object to border shorthand
 * This currently works fine if every value uses an alias,
 * but if any one of these use a raw value, it will not be transformed.
 */
function transformBorderForCSS(border) {
    if (typeof border !== 'object') {
        return border;
    }
    const { color, width, style } = border;
    return `${transformDimension.transformDimension(checkAndEvaluateMath.checkAndEvaluateMath(width))} ${style} ${transformHEXRGBa.transformHEXRGBaForCSS(color)}`;
}

exports.transformBorderForCSS = transformBorderForCSS;
