'use strict';

var percentageToDecimal = require('../utils/percentageToDecimal.cjs');

/**
 * Helper: Transforms letter spacing % to em
 */
function transformLetterSpacingForCSS(value) {
    if (value === undefined) {
        return value;
    }
    const decimal = percentageToDecimal.percentageToDecimal(value);
    return typeof decimal === 'string' || isNaN(decimal) ? `${value}` : `${decimal}em`;
}

exports.transformLetterSpacingForCSS = transformLetterSpacingForCSS;
