'use strict';

var percentageToDecimal = require('./utils/percentageToDecimal.cjs');

/**
 * Helper: Transforms line-height % to unit-less decimal value
 * @example
 * 150% -> 1.5
 */
function transformLineHeight(value) {
    if (value === undefined) {
        return value;
    }
    const decimal = percentageToDecimal.percentageToDecimal(value);
    return typeof decimal === 'string' || isNaN(decimal) ? `${value}` : `${decimal}`;
}

exports.transformLineHeight = transformLineHeight;
