'use strict';

var percentageToDecimal = require('./utils/percentageToDecimal.cjs');

/**
 * Helper: Transforms opacity % to a decimal point number
 * @example
 * 50% -> 0.5
 */
function transformOpacity(value) {
    if (value === undefined) {
        return value;
    }
    const decimal = percentageToDecimal.percentageToDecimal(value);
    return typeof decimal === 'string' || isNaN(decimal) ? `${value}` : `${decimal}`;
}

exports.transformOpacity = transformOpacity;
